import React, { Component } from 'react';
import BookCard from './book-tiles';

class Form extends Component {
    constructor() {
        super();

        this.state = {
          book: { 
            title: '',
            author: '',
            pages: '',
            id: book.title + book.pages,
          },
          library: []
        };
      }

      handleTitle = (e) => {
          this.setState({
              book: {
                  title: e.target.value,
              }
          })
      }

      handleAuthor = (e) => {
        this.setState({
            book: {
                author: e.target.value,
            }
        })
    }

    handlePages = (e) => {
        this.setState({
            book: {
                pages: e.target.value,
                id: this.state.book.id,
            }
        })
    }

    onSubmitBook = (e) => {
        e.preventDefault();
        this.setState({
            library: this.state.library.concat(this.state.book),
            book: {
                title: '',
                author: '',
                pages: '',
                id: book.title + book.pages,
            },
        })
    }

    render() {
        const { book, library} = this.state;

        return (
            <div id='input-form-div'>
                <BookCard libraryArray={library}/>
                <form onSubmit={this.onSubmitBook}>
                    <label>
                        Book Title
                    </label>
                    <input 
                        onChange={this.handleTitle}
                        value={book.title}
                        type="text"
                    />
                    <label>
                        Author
                    </label>
                    <input
                        onChange={this.handleAuthor}
                        value={book.author}
                        type="text"
                    />
                    <label>
                        Number of Pages
                    </label>
                    <input
                        onChange={this.handlePages}
                        value={book.pages}
                        type="number"
                    />
                    <button type='submit'>
                        ADD
                    </button>
                </form>
            </div>
        )
    }
}

export default Form;
