import React, { Component } from 'react';
import BookCard from './components/book-tiles';
import uniqid from 'uniqid';

class Form extends Component {
    constructor() {
        super();

        this.state = {
          book: { 
            title: '',
            author: '',
            pageNum: '',
            id: uniqid(),
          },
          library: []
        };
      }

    render() {
        const {library} = this.state;
        return (
    <div id='input-form-div'>
        <BookCard libraryArray={library}/>
        <form>
            <label>
                Book Title
            </label>
            <input></input>
            <label>
                Author
            </label>
            <input></input>
            <label>
                Number of Pages
            </label>
            <input></input>
            <button type='submit'>
                ADD
            </button>
        </form>
    </div>
        )
    }
}

export default Form;
