import React from 'react';

function BookCard(props) {
    const { libraryArray, handleChange } = props;
    // const onChange = () => {
    //     return handleChange;
    // }
    // function changeReadStatus(e) {
    //     let key = e.target.dataset.key;
    //     console.log(e.target);
    //     let book = libraryArray.find(theKey);
    //     console.log(book);
    //     function theKey(book) {
    //         return book.id === key;
    //     }
    //     if (book.bookRead === "read") {
    //         book.bookRead = "unread";
    //     } else {
    //         book.bookRead = "read"
    //     }
    // }
    function handleSubmit(e) {
        e.preventDefault();
    }
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <form key={book.id} data-key={book.id} onSubmit={handleSubmit}>
                    {`${book.title} by ${book.author} has ${book.pages} pages and this book is ${book.bookRead}`} 
                    <br />
                    <label>
                    Change read status?
                    <select onChange={handleChange} data-key={book.id} >
                        <option value="">--select--</option>
                        <option value={book.bookRead === "read" ? "unread" : "read"}>yes</option>
                    </select>
                    </label>
                    <input type="submit" value="OKAY" onClick={handleChange}/>
                    </form>
                })}
            </section>
        </div>
    )
}

export default BookCard;
