import React from 'react';

const BookCard = (props) => {
    const { libraryArray } = props;
    function changeReadStatus(e) {
        let key = e.target.dataset.key
        function theKey(book) {
            return book.id === key;
        }
        let book = libraryArray.find(theKey);
        if (book.bookRead === "read") {
            book.bookRead = "unread";
        } else {
            book.bookRead = "read"
        }
    }
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <div key={book.id} data-key={book.id}>{`${book.title} by ${book.author} has ${book.pages} pages and this book is ${book.bookRead}.`} <ChangeReadStatusSelect /> </div>
                })}
            </section>
        </div>
    )
}

const ChangeReadStatusSelect = () => {
    handleSubmit = (e) => {
        e.preventDefault();
        console.log("submit");
    }
    this.handleSubmit = this.handleSubmit.bind(this);

    return(
        <form onSubmit={this.handleSubmit}>
            <label>
            This book is:
            <br />
            <select
                value={"read"}
                onChange={console.log("change")}
                required
            >
            <option value="">--select one--</option>
            <option value={"read"}>read</option>
            <option value={"unread"}>unread</option>
            </select>
        </label>
        <input type="submit" value="Submit"/>
        </form>
    )
}

export default BookCard;
