import React from 'react';

class BookCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            book: {
                title: props.book.title,
                author: props.book.author,
                pages: props.book.pages,
                bookRead: props.book.bookRead,
                id: props.book.id,
            },
            removeBook: props.removeBook,
            hideEditForm: true,
        }
        this.updateReadStatus = this.updateReadStatus.bind(this);
        this.deleteBook = this.deleteBook.bind(this);
    }

    deleteBook = (e) => {
        return this.state.removeBook(e);
    }

    handleChange = (e) => {
        const value = e.target.value;
        const name = e.target.name;

        this.setState({
            book: {
                [name]: value,
            },
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.toggleEditFormVisibility();
    }

    updateReadStatus = () => {
        if (this.state.book.bookRead === "read") {
            this.setState({ 
                book: { 
                    title: this.state.book.title,
                    author: this.state.book.author,
                    pages: this.state.book.pages,
                    bookRead: "unread", 
                    id: this.state.book.id,
                } 
            })
        } else {
            this.setState({ 
                book: { 
                    title: this.state.book.title,
                    author: this.state.book.author,
                    pages: this.state.book.pages,
                    bookRead: "read", 
                    id: this.state.book.id,
                } 
            })
        }
    }

    toggleEditFormVisibility = () => {
        this.state.hideEditForm === true ? this.setState({hideEditForm: false}) : this.setState({hideEditForm: true});
    }

    render() {
        return (
            <div id='book-child-div'>
                {`${this.state.book.title} by ${this.state.book.author} has ${this.state.book.pages} pages and is ${this.state.book.bookRead}`}
                <button onClick={this.updateReadStatus}>
                    Change Read Status
                </button>
                <button onClick={this.toggleEditFormVisibility}>
                    Edit Book
                </button>
                {this.state.hideEditForm === true ? null : 
                    <form id='add-a-book-form' onSubmit={this.handleSubmit}>
                    <label>
                        Book Title
                    <input 
                        name="title"
                        type="text"
                        value={this.state.book.title}
                        onChange={this.handleChange}
                        required
                    />
                    </label>
                    <label>
                        Author
                    <input
                        name="author"
                        type="text"
                        value={this.state.book.author}
                        onChange={this.handleChange}
                        required
                    />
                    </label>
                    <label>
                        Number of Pages
                    <input
                        name="pages"
                        type="number"
                        value={this.state.book.pages}
                        onChange={this.handleChange}
                        required
                    />
                    </label>
                    <button data-key={this.state.book.id} onClick={this.deleteBook}>
                    Remove Book
                    </button>
                    <br />
                    <button type='submit'>
                        EDIT
                    </button>
                </form>
                }
            </div>
        )
    }
}

export default BookCard;
