import React from 'react';

const BookCard = (props) => {
    const { libraryArray, handleChange } = props;
    function changeReadStatus(e) {
        let key = e.target.dataset.key
        function theKey(book) {
            return book.id === key;
        }
        let book = libraryArray.find(theKey);
        if (book.bookRead === "read") {
            book.bookRead = "unread";
            return handleChange;
        } else {
            book.bookRead = "read"
            return handleChange;
        }
    }
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <div key={book.id}>{`${book.title} by ${book.author} has ${book.pages} pages and this book is ${book.bookRead}.`} <button data-key={book.id} onClick={changeReadStatus}>Change Read Status</button></div>
                })}
            </section>
        </div>
    )
}



export default BookCard;
