import React from 'react';

const BookCard = (props) => {
    const { libraryArray } = props;
    function onClick(e) {
        let key = e.target.dataset.key
        function theKey(book) {
            return book.id === key;
        }
        let book = libraryArray.find(theKey);
        let readStatus = book.bookRead;
        console.log(readStatus);
        // if (book.bookRead === "true") {
        //     book.bookRead = "false"
        // } else {
        //     bookRead = "true"
        // }
    }
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <div key={book.id}>{`${book.title} by ${book.author} has ${book.pages} pages and this book is ${book.bookRead === "true" ? "read" : "unread"}.`} <button data-key={book.id} onClick={onClick}>Change Read Status</button></div>
                })}
            </section>
        </div>
    )
}



export default BookCard;
