import React, { useState } from 'react';
import BookCard from './book-tiles';
import uniqid from 'uniqid';

const initialValues = {
    book: {
        title: '',
        author: '',
        pages: '',
        id: uniqid()
    }
}

export default function Form() {
    const [book, setValues] = useState(initialValues);

    return (
        <div id='input-form-div'>
            <BookCard libraryArray={library}/>
            <form onSubmit={this.onSubmitBook}>
                <label>
                    Book Title
                <input 
                    name="title_input"
                    type="text"
                    value={book.title}
                    onChange={this.handleChange}
                    required
                />
                </label>
                <label>
                    Author
                <input
                    name="author_input"
                    type="text"
                    value={book.author}
                    onChange={this.handleChange}
                    required
                />
                </label>
                <label>
                    Number of Pages
                <input
                    name="pages_input"
                    type="number"
                    value={book.pages}
                    onChange={this.handleChange}
                    required
                />
                </label>
                <button type='submit' value="Submit">
                    ADD
                </button>
            </form>
        </div>
    )
}
