import React, { Component } from 'react';
import BookCard from './book-tiles';
import uniqid from 'uniqid';

class Form extends Component {
    constructor(props) {
        super(props);
        this.state = {
            library: [],
            bookTitle: '',
            bookAuthor: '',
            bookPages: '',
            isBookRead: '',
            book: {
                title: '',
                author: '',
                pages: '',
                bookRead: '',
                id: uniqid(),
            },
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (e) => {
        const value = e.target.value;
        const name = e.target.name;

        this.setState({
            [name]: value,
            book: {
                bookRead: this.state.isBookRead,
                title: this.state.bookTitle,
                author: this.state.bookAuthor,
                pages: this.state.bookPages,
                id: this.state.book.id,
            },
        });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({
            // book: {
            //     title: this.state.bookTitle,
            //     author: this.state.bookAuthor,
            //     pages: this.state.bookPages,
            //     bookRead: this.state.isBookRead,
            //     id: this.state.book.id,
            // },
            library: this.state.library.concat(this.state.book),
            bookTitle: '',
            bookAuthor: '',
            bookPages: '',
            isBookRead: '',
            book: {
                title: '',
                author: '',
                pages: '',
                bookRead: '',
                id: uniqid(),
            },
        });
    }

    render() {
        const { library } = this.state;
        return (
            <div id='input-form-div'>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Book Title
                    <input 
                        name="bookTitle"
                        type="text"
                        value={this.state.bookTitle}
                        onChange={this.handleChange}
                        required
                    />
                    </label>
                    <label>
                        Author
                    <input
                        name="bookAuthor"
                        type="text"
                        value={this.state.bookAuthor}
                        onChange={this.handleChange}
                        required
                    />
                    </label>
                    <label>
                        Number of Pages
                    <input
                        name="bookPages"
                        type="number"
                        value={this.state.bookPages}
                        onChange={this.handleChange}
                        required
                    />
                    </label>
                    <label>
                    This book is:
                    <br />
                    <select 
                        name="isBookRead"
                        value={this.state.isBookRead}
                        onChange={this.handleChange}
                        required
                    >
                    <option value="">--select one--</option>
                    <option value="true">read</option>
                    <option value="false">unread</option>
                    </select>
                    </label>
                    <br />
                    <button type='submit' value="Submit">
                        ADD
                    </button>
                </form>
                <BookCard libraryArray={library} />
            </div>
        )
    }
}

export default Form;
