import React from 'react';

const BookCard = (props) => {
    const libraryArray = props.libraryArray;
    console.log(libraryArray);
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <div key={book.id}>{`${book.title} by ${book.author} has ${book.pages} pages and this book is ${book.readStatus === true ? "unread" : "read"}.`} <button onClick={changeReadStatus(book)}>Change Book Read Status</button></div>
                })}
            </section>
        </div>
    )
}

const changeReadStatus = (book) => {
    if (book.bookRead === true) {
        this.setState({
            book: {
                bookRead: false,
            }
        });
    } else {
        this.setState({
            book: {
                bookRead: true,
            }
        });
    }
}

export default BookCard;
