import React from 'react';

const BookCard = (props) => {
    const { bookArray, libraryArray} = props;
    console.log("Book Array:" + bookArray);
    if (bookArray.length > 0) {
        this.setState({
            libraryArray: this.state.libraryArray.concat(bookArray[0]),
        });
    }
    console.log("Library Array:" + libraryArray);
    return(
        <ul>
            {libraryArray.map((book) => {
                return <li key={book.id}>{book}</li>;
            })}
        </ul>
    )

}

export default BookCard;
