import React from 'react';
import ChangeReadStatus from './change-read-status';

const BookCard = (props) => {
    const { libraryArray } = props;
    console.log(libraryArray);
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <div key={book.id}>{`${book.title} by ${book.author} has ${book.pages} pages and this book is ${book.bookRead === "true" ? "read" : "unread"}.`} <button data-key={book.id} onClick={ChangeReadStatus(book)}>Change Read Status</button></div>
                })}
            </section>
            <ChangeReadStatus />
        </div>
    )
}

export default BookCard;
