import React from 'react';

class BookCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            library: props.library,
            book: {
                title: props.book.title,
                author: props.book.author,
                pages: props.book.pages,
                bookRead: props.book.bookRead,
                id: props.book.id,
            },
        }
        this.updateReadStatus = this.updateReadStatus.bind(this);
    }

    updateReadStatus(props) {
        if (this.state.book.bookRead === "read") {
            this.setState({ 
                book: { 
                    // title: props.book.title,
                    // author: props.book.author,
                    // pages: props.book.pages,
                    bookRead: "unread", 
                    // id: props.book.id,
                } 
            })
        } else {
            this.setState({ 
                book: { 
                    // title: props.book.title,
                    // author: props.book.author,
                    // pages: props.book.pages,
                    bookRead: "read", 
                    // id: props.book.id,
                } 
            })
        }
    }

    componentWillUnmount() {
        this.setState({ library: this.state.library.filter(book => book.id !== this.state.book.id) })
    }

    render() {
        return (
            <div>
                {`${this.state.book.title} by ${this.state.book.author} has ${this.state.book.pages} pages and is ${this.state.book.bookRead}`}
                <button onClick={this.updateReadStatus}>
                    Change Read Status
                </button>
                <button onClick={this.componentWillUnmount}>
                    Remove Book
                </button>
            </div>
        )
    }
}

export default BookCard;
