import React from 'react';

const BookCard = (props) => {
    const { libraryArray } = props;
    function changeReadStatus(e) {
        e.preventDefault();
        let key = e.target.dataset.key
        console.log(key);
        function theKey(book) {
            return book.id === key;
        }
        let book = libraryArray.find(theKey);
        if (book.bookRead === "read") {
            book.bookRead = "unread";
        } else {
            book.bookRead = "read"
        }
    }
    return(
        <div>
            <section>
                {libraryArray.map((book) => {
                    return <form key={book.id} onSubmit={changeReadStatus}>
                    {`${book.title} by ${book.author} has ${book.pages} pages and this book is `} 
                    <select data-key={book.id}>
                        <option data-key={book.id} value={book.bookRead === "read" ? "read" : "unread"}>{book.bookRead}</option>
                        <option data-key={book.id} value={book.bookRead === "read" ? "unread" : "read"}>{book.bookRead === "read" ? "unread" : "read"}</option>
                    </select>
                    <input data-key={book.id} type="submit" value="Submit"/>
                    </form>
                })}
            </section>
        </div>
    )
}

export default BookCard;
